import { Link, useNavigate } from 'react-router-dom'
import { useState } from "react"
import './login.scss'

const Login = () => {
    const navigate = useNavigate()

    const [inputs, setInputs] = useState({
        "username" : "",
        "password" : ""
    })
    const [err,setErr] = useState(null)

    const handleChange = (e) => {
        setInputs((prev) => {
            return{...prev, [e.target.name] : e.target.value}
        })
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        try{
            await Login(inputs).then(
                navigate("/home")
            )
        }catch(err){
            setErr(err)
            console.log(err)
        }
    }

    return (
        <div className='center'>
            <div className='login'>
                <div className='left'>
                    <h1>Ready to trade something old,
                        for something new!</h1>
                    <form>
                        <input onChange={handleChange} className="login-input" type="text" placeholder="Username"></input>
                        <input onChange={handleChange} className="login-input" type="password" placeholder="Password"></input>
                    </form>
                    <span>
                    </span>
                    <button onClick={handleSubmit}>Login</button>
                    <p>Want to create an account. Sign up here! </p>

                    <Link to={"/register"}>
                        <button>Create Your Account</button>
                    </Link>

                </div>

            </div>
        </div>
    )
}

export default Login
